import React , { Fragment, useState, useEffect } from 'react';

interface ITodo {
    text: string
    complete: boolean
  }

export default function TodoList(props: any): JSX.Element{

    const complete = (index: number): void => {
        props.todos[index].complete = !props.todos[index].complete
        props.setTodos([...props.todos])
      }

    return (
        <section>
        {props.todos.map((todo: ITodo, index: number) => (
          <Fragment key={index}>
            <div>
              {todo.text} {' '}

                {
                  props.todos[index].complete ? 

                  <button type='button' onClick={() => complete(index)}>TODO</button>

                  : 

                  <button type='button' onClick={() => complete(index)}>Complete</button>
                }
            </div>     
          </Fragment>
        ))}
      </section>
    )

}
