import React , { Fragment, useState, useEffect } from 'react';
import './App.css';
import { Typography, Grid } from '@material-ui/core';

interface ITodo {
  text: string
  complete: boolean
}

export default function App(): JSX.Element{
  type FormElem = React.FormEvent<HTMLFormElement>;

  const [value, setValue] = useState('');
  const [todos, setTodos] = useState<ITodo[]>([]);

  useEffect(() => {
  });

  const handleSubmit = (e: FormElem) => {
    e.preventDefault();
    addTodo(value);
    setValue('');
  }

  const addTodo = (text:string) => {
    setTodos([...todos, {text: text, complete: false}])
  }

  const complete = (index: number): void => {
    todos[index].complete = !todos[index].complete
    setTodos([...todos])
  }

  return (
    <Fragment>

      <Grid container spacing={0}>
        <Grid item xs={12} style={{textAlign: 'center'}}>
          <Typography variant="h3">Todo List</Typography>
        </Grid>
      </Grid>
      <form onSubmit={handleSubmit}>
        <input type="text" required value={value} onChange={e => setValue(e.target.value)}/>

        <button type="submit">Add Todo</button>
      </form>

      <section>
        {todos.map((todo: ITodo, index: number) => (
          <Fragment key={index}>
            <div>
              {todo.text} {' '}

                {
                  todos[index].complete ? 

                  <button type='button' onClick={() => complete(index)}>TODO</button>

                  : 

                  <button type='button' onClick={() => complete(index)}>Complete</button>
                }
            </div>     
          </Fragment>
        ))}
      </section>
    </Fragment>
  );
}
