import React , { Fragment, useState, useEffect } from 'react';
import './App.css';
import { Typography, Grid, TextField, Fab } from '@material-ui/core';
import AddIcon from '@material-ui/icons/Add';

interface ITodo {
  text: string
  complete: boolean
}

export default function App(): JSX.Element{
  type FormElem = React.FormEvent<HTMLFormElement>;

  const [value, setValue] = useState('');
  const [todos, setTodos] = useState<ITodo[]>([]);

  useEffect(() => {
  });

  const handleSubmit = (e: FormElem) => {
    e.preventDefault();
    addTodo(value);
    setValue('');
  }

  const addTodo = (text:string) => {
    setTodos([...todos, {text: text, complete: false}])
  }

  const complete = (index: number): void => {
    todos[index].complete = !todos[index].complete
    setTodos([...todos])
  }

  return (
    <Fragment>

      <Grid container spacing={6}>
        <Grid item xs={12} style={{textAlign: 'center'}}>
          <Typography variant="h3">Todo List</Typography>
        </Grid>
      </Grid>
      <form onSubmit={handleSubmit}>
        {/* <input type="text" required value={value} onChange={e => setValue(e.target.value)}/> */}
        <Grid container spacing={3}>
          <Grid item xs={8} style={{textAlign: 'center'}}>
            <TextField id="outlined-basic" label="AÃ±ade un producto" variant="outlined" value={value} onChange={e => setValue(e.target.value)}/>  
          </Grid>
          <Grid item xs={4}>
          <Fab color="primary" aria-label="add" onClick={() => handleSubmit}>
              <AddIcon />
            </Fab>

          </Grid>
        </Grid>



      </form>

      <section>
        {todos.map((todo: ITodo, index: number) => (
          <Fragment key={index}>
            <div>
              {todo.text} {' '}

                {
                  todos[index].complete ? 

                  <button type='button' onClick={() => complete(index)}>TODO</button>

                  : 

                  <button type='button' onClick={() => complete(index)}>Complete</button>
                }
            </div>     
          </Fragment>
        ))}
      </section>
    </Fragment>
  );
}
