import React , { Fragment, useState, useEffect } from 'react';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

interface ITodo {
    text: string
    complete: boolean
  }

export default function TodoList(props: any): JSX.Element{

    const complete = (index: number): void => {
        props.todos[index].complete = !props.todos[index].complete
        props.setTodos([...props.todos])
      }

    return (
        <section>
        {props.todos.map((todo: ITodo, index: number) => (
          <Fragment key={index}>
            <div>
              {todo.text} {' '}

                {
                  props.todos[index].complete ? 

                  <button type='button' onClick={() => complete(index)}>TODO</button>

                  : 

                  <button type='button' onClick={() => complete(index)}>Complete</button>
                }
            </div>     
          </Fragment>
        ))}

        <Card className={classes.card}>
        <CardContent>
            <Typography className={classes.title} color="textSecondary" gutterBottom>
            Word of the Day
            </Typography>
            <Typography variant="h5" component="h2">
            be{bull}nev{bull}o{bull}lent
            </Typography>
            <Typography className={classes.pos} color="textSecondary">
            adjective
            </Typography>
            <Typography variant="body2" component="p">
            well meaning and kindly.
            <br />
            {'"a benevolent smile"'}
            </Typography>
        </CardContent>
        <CardActions>
            <Button size="small">Learn More</Button>
        </CardActions>
        </Card>



      </section>
    )

}
